INSERT INTO pizzas (name, description, img, price, created_at)VALUES('Margherita', 'La pizza Margherita è un classico italiano con una base sottile, pomodoro fresco, mozzarella di bufala e basilico. La combinazione di sapori freschi e semplici la rende un piacere culinario iconico.', 'https://media.gettyimages.com/id/1414575281/it/foto/una-deliziosa-e-gustosa-pizza-italiana-margherita-con-pomodori-e-mozzarella-di-bufala.jpg?s=612x612&w=0&k=20&c=NTxFTkkUXQu13aoDy8vW9SK27GsqOufOPNWpjycCmok=', 7.50, '2023-11-09 11:35:00');
INSERT INTO pizzas (name, description, img, price, created_at)VALUES('Capricciosa', 'La pizza capricciosa, una sinfonia di sapori e colori sulla base croccante: pomodoro, mozzarella, funghi, prosciutto cotto e olive nere. Un capriccio delizioso per il palato!', 'https://media.gettyimages.com/id/1334232825/it/foto/a-platter-of-wood-fired-capricciosa-pizza.jpg?s=612x612&w=0&k=20&c=zH7OlL6vlvg3dqY0ksCYn5OvkHor8trlGR8XePBTTds=', 8.50, '2023-11-09 15:35:00');
INSERT INTO pizzas (name, description, img, price, created_at)VALUES('Quattro Formaggi', "Un'esplosione di formaggi prelibati su una crosta dorata. Gorgonzola, mozzarella, parmigiano e taleggio si fondono armoniosamente, creando un paradiso caseario.", 'https://media.gettyimages.com/id/1251763701/it/foto/nourriture-snacking-pizza-4-fromages.jpg?s=612x612&w=0&k=20&c=ie3dK6A_-JnaCrDS8G0w82l5u-Whv4hZCmR4tirVVhw=', 7.00, '2023-11-09 15:35:00');
INSERT INTO pizzas (name, description, img, price, created_at)VALUES('Primavera Veggie', "Una sinfonia di verdure fresche su una base croccante. Pomodori succulenti, zucchine tenere, peperoni colorati e cipolle dolci si combinano per creare un'esplosione di sapori primaverili.", 'https://media.gettyimages.com/id/1290890447/it/foto/food-photography-of-healthy-green-spring-vegetarian-pizza-with-asparagus-beans-cheese-and.jpg?s=612x612&w=0&k=20&c=eaBAokA0POFgQArpljQMF6l63M-bXReEGTkRapKpHzo=', 8.50, '2023-11-10 12:45:00');
INSERT INTO pizzas (name, description, img, price, created_at)VALUES('Mediterranea Delizia', "Gustosi ingredienti mediterranei si uniscono su una base sottile. Olive nere, feta cremosa, pomodori secchi e origano fresco creano un'esperienza culinaria ispirata alla costa.", 'https://media.gettyimages.com/id/1497887381/it/foto/famiglia-che-gode-della-pizza-tradizionale-calabrese-per-il-pranzo.jpg?s=612x612&w=0&k=20&c=KRHfjYFKhHTXNZp9HNOHYwNBN52WKduVvSzPKzg-GaY=', 9.50, '2023-11-10 14:20:00')
INSERT INTO pizzas (name, description, img, price, created_at)VALUES('Piccante Inferno', "Un mix ardente di peperoncini e formaggi piccanti su una crosta che promette fuoco e passione. Jalapeños, peperoncini rossi, pepperoni e formaggio cheddar fanno di questa pizza una scelta audace.", 'https://media.gettyimages.com/id/1307010710/it/foto/freshly-made-tasty-pizza-salami-or-pepperoni-with-fresh-basil-on-top.jpg?s=612x612&w=0&k=20&c=CUXuixdT5KP5Zrtr0WSErv1MdVUkYVuPWnWyWtBKAi8=', 10.00, '2023-11-10 16:05:00')
INSERT INTO pizzas (name, description, img, price, created_at)VALUES('Terra Toscana', "I sapori della Toscana prendono vita su questa pizza. Funghi porcini, salsiccia toscana, pomodori freschi e parmigiano reggiano si mescolano in una deliziosa danza di gusti autentici.", 'https://media.gettyimages.com/id/1314542729/it/foto/fresh-baked-pizza-with-cutter.jpg?s=612x612&w=0&k=20&c=k2JldDi4TsKU3xq78ZQUpPpysWm3kjTSsn8qB9NLy4c=', 9.80, '2023-11-10 18:30:00')
INSERT INTO pizzas (name, description, img, price, created_at)VALUES('Rustica Campagna', "Un viaggio nei campi con questa pizza rustica. Patate al forno, rosmarino, cipolle caramellate e pancetta croccante si uniscono per portare il gusto della campagna direttamente sulla tua tavola.", 'https://media.gettyimages.com/id/1037020170/it/foto/pizza-fatta-in-casa.jpg?s=612x612&w=0&k=20&c=mrRDm3dm-zh83cQgrkro66HSgHumTsGIWO1lTfNE5rQ=', 11.20, '2023-11-10 20:15:00')
INSERT INTO pizzas (name, description, img, price, created_at)VALUES('Formaggio di Capra e Miele', "Una combinazione sofisticata di formaggio di capra cremoso e miele dolce su una base sottile. Noci tostate e rucola fresca completano questa pizza elegante e deliziosa.", 'https://media.gettyimages.com/id/1305943860/it/foto/melted-cheese-stretches-on-slice-of-hot-pizza-woman-takes-a-slice-of-pizza-delicious-pizza.jpg?s=612x612&w=0&k=20&c=7m7M_DjFzJWiKpl4xI8wJoNSphE2flp_t3zOOzMh4b8=', 12.50, '2023-11-10 22:40:00')
INSERT INTO pizzas (name, description, img, price, created_at)VALUES('Mare Azzurro', "Il sapore fresco del mare su ogni fetta. Gamberi succulenti, cozze, pomodori ciliegini e basilico si uniscono per creare una pizza che ti trasporterà direttamente sulle coste del Mediterraneo.", 'https://media.gettyimages.com/id/1500978280/it/foto/pizza-pesto-genovese.jpg?s=612x612&w=0&k=20&c=c22DlBxEh131G5bJTyoAqzZ6Bmv8AnD48R76Xwxp_Kg=', 13.70, '2023-11-11 10:00:00')
INSERT INTO pizzas (name, description, img, price, created_at)VALUES('Montagna Alpina', "Un'esplosione di sapori alpini su una base spessa. Patate, speck, cipolle rosse e formaggio gruyère si combinano per creare una pizza robusta e avvolgente.", 'https://media.gettyimages.com/id/1486553545/it/foto/pizza-home-made.jpg?s=612x612&w=0&k=20&c=EBwGks6X9idzNwjVhHyAEgBmGEpwbWUGc6wfS58iCBs=', 11.80, '2023-11-11 12:25:00')
INSERT INTO pizzas (name, description, img, price, created_at)VALUES('Tropicale Paradise', "Una festa tropicale di gusti su una crosta dorata. Ananas succoso, prosciutto, peperoni colorati e formaggio mozzarella si fondono in un'armonia di dolce e salato.", 'https://media.gettyimages.com/id/1337988677/it/foto/talk-a-piece-of-pizza.jpg?s=612x612&w=0&k=20&c=1dWOxpPzd5O0Is4_7dcIACrtLlAVc1xdvc32IinqOzQ=', 10.90, '2023-11-11 14:50:00')
